import React, { useState } from 'react';
import "./App.css"

const InputItem = ({appendTodo}) => {
  const [value, setValue] = useState("");

  const onChangeHandler = (e) => {
    setValue(e.target.value);
  };

  return (<>
    <input value={value} onChange={onChangeHandler}></input>
    <button onClick={e => {
      appendTodo(value);
      setValue("");
    }}>확인</button>
  </>);
}

const TodoList = () =>
{
  const [value, setValue] = useState("dddd");
  const [todoList, setTodoList] = useState([
    {no:1, title:"치맥 하기", done:false},
    {no:2, title:"방 청소 하기", done:false},
    {no:3, title:"명상 하기", done:true},
    {no:4, title:"착한 일 하기", done:false}
  ]);
  
  const [cnt, setCnt] = useState(1);

 


  const deleteHandler = (e) =>
  {

  }


  const appendTodo = (title) => {
    // 할일 목록에 새 항목을 추가.
    let newItem = {no:cnt, title:title, done:false};
    setCnt(cnt + 1);
    setTodoList([...todoList, newItem]);
  }

  const exceptTodo = (no) =>
  {
    let index = todoList.findIndex(function(item)
    {
      return Number(no) == Number(item.no);
    });
    let newList = [...todoList];
    newList.splice(index, 1);
    setTodoList(newList);
  }

  let list = todoList.map((item) => {
    return(
    <li key={item.no}>
        <input type='checkbox'/>
        <label>{item.title}</label>
        <button onClick={deleteHandler}>삭제</button>
    </li>);
    })

  return (
    <div>
      <div className='App-header'>
        <h1>Todo List</h1>
      </div>
      <main>
        <h2>welcome</h2>
        <InputItem appendTodo={appendTodo} />
        <hr/>
        <ul>
          {list}
        </ul>
      </main>
      
      <footer>
        (c)Comstudy21. since 2024.
      </footer>
    </div>
  )
}

const App = () =>{
  return(
    <div className = "App">
      <TodoList/>
    </div>
    
  )
}

export default App;
