import React, { useState } from 'react';
import "./App.css"

const TodoList = () =>
{
  const [value, setValue] = useState("dddd");
  const [todoList, setTodoList] = useState([
    {no:1, title:"치맥 하기", done:false},
    {no:2, title:"방 청소 하기", done:false},
    {no:3, title:"명상 하기", done:true},
    {no:4, title:"착한 일 하기", done:false}
  ]);

  const onchangeHandler = (e) =>
  {
      setValue(e.target.value)
  }

  return (
    <div>
      <div className='App-header'>
        <h1>Todo List</h1>
      </div>
      <main>
        <h2>welcome</h2>
        <input value={value} onChange={onchangeHandler}></input>
        <button>입력</button>
        <hr/>
        <ul>
          <li>
            <input type="checkbox"/>
            <label>aaaaaa</label>
            <button>삭제</button>
          </li>
        </ul>
      </main>
      
      <footer>
        (c)Comstudy21. since 2024.
      </footer>
    </div>
  )
}

const App = () =>{
  return(
    <div className = "App">
      <TodoList/>
    </div>
    
  )
}

export default App;
